     , manager_name
     , manager_phone
     , manager_email
     , nation
     , need_time
     , consulting_request_time
     , consulting_request_time_detail
     , consulting_request_time_etc
     , consulting_subject
     , core_question
     , time_need_advisory
     , time_need_advisory_etc
     , info_agree_yn
     , use_agree_yn
) VALUES (
       'SUCCESS'
     , #{id}
     , #{password}
     , #{companyName}
     , #{companyCategory}
     , #{companyCategoryEtc}
     , #{managerName}
     , #{managerPhone}
     , #{managerEmail}
     , #{nation}
     , #{needTime}
     , #{consultingRequestTime}
     , #{consultingRequestTimeDetail}
     , #{consultingRequestTimeEtc}
     , #{consultingSubject}
     , #{coreQuestion}
     , #{timeNeedAdvisory}
     , #{timeNeedAdvisoryEtc}
     , #{infoAgreeYn}
     , #{useAgreeYn}
