mapper.select:
SELECT IFNULL(MAX(SUBSTRING(custNo, 7, 4)), 0) + 1
FROM user_tb
WHERE SUBSTRING(custNo, 1, 6) = #{today}

mapper.insert:
INSERT INTO user_tb (
    custNo
    , cardId
    , custName
    , webId
    , password
    , cDate
) VALUES (
             #{custNo}
             , #{cardId}
             , #{custName}
             , #{webId}
             , #{password}
             , NOW()
         )

mapper.select:
SELECT *
FROM user_tb
WHERE webId = #{webId} AND webPw = #{webPw}
