mapper.insert:
INSERT INTO t_qna
(
    TITLE
    , CONTENT
    , PASSWORD_YN
    , PASSWORD
    , CREATE_ID
    , CREATE_DT
    , VIEW_CNT
) VALUES (
         #{title}
        , #{content}
        , #{passwordYn}
        , #{password}
        , #{createId}
        , NOW()
        , 0
    )

mapper.select:
SELECT password
FROM t_qna
WHERE qna_id = #{qnaId}

mapper.update:
UPDATE t_qna
SET view_cnt = view_cnt + 1
WHERE qna_id = #{qnaId}

UPDATE user_tb
SET webPw = #{webPw}
WHERE custNo = #{custNo}
