mapper.select:
SELECT fDate , date_format(cDate, '%H:%i:%s') as createDt , amnt FROM mkt_tb
gbn = 'CHARGE' and custNo = #{custNo} and rtn = '01'
AND date_format(cDate, '%Y-%m-%d') >= #{searchStart}
AND date_format(cDate, '%Y-%m-%d') <= #{searchEnd}
ORDER BY cDate DESC

SELECT COUNT(*)
FROM mkt_tb

    gbn = 'CHARGE' and custNo = #{custNo} and rtn = '01'

        AND date_format(cDate, '%Y-%m-%d') >= #{searchStart}


        AND date_format(cDate, '%Y-%m-%d') <= #{searchEnd}

SELECT mdt.fDate , date_format(mdt.cDate, '%H:%i:%s') as createDt , mdt.gbn , mdt.amnt , mdt.balance , mdt.menuNm FROM mkt_desc_tb mdt
mdt.custNo = #{custNo} and mdt.rtn = '01'
AND date_format(mdt.cDate, '%Y-%m-%d') >= #{searchStart}
AND date_format(mdt.cDate, '%Y-%m-%d') <= #{searchEnd}
ORDER BY mdt.cDate DESC

SELECT COUNT(*)
  FROM mkt_desc_tb mdt

    mdt.custNo = #{custNo} and mdt.rtn = '01'

        AND date_format(mdt.cDate, '%Y-%m-%d') >= #{searchStart}


        AND date_format(mdt.cDate, '%Y-%m-%d') <= #{searchEnd}

mapper.update:
UPDATE mkt_desc_tb
   SET balance = #{balance}
 WHERE fDate = #{fDate}
   AND rtn = #{rtn}
   AND ilno = #{ilno}
   AND snno = #{snno}
   AND loc = #{loc}
